function hoverVideo1(){
	document.getElementById('VI1').style.backgroundImage="url(video1-hover.png)";
	document.getElementById('Btn1').style.opacity="1";
}

function afterVideo1(){
	document.getElementById('VI1').style.backgroundImage="url(video1.png)";
	document.getElementById('Btn1').style.opacity="0.8";
}
